mapper.select:
SELECT COUNT(*)
FROM TB_mst
WHERE mst_cd = #{mst_cd}

SELECT mst_cd, mst_name, mst_notes
FROM TB_mst  
WHERE mst_cd = #{mst_cd}

mapper.update:
UPDATE TB_mst
SET mst_name=#{mst_name}, mst_notes=#{mst_notes}, upd_date=NOW()
WHERE mst_cd = #{mst_cd}

mapper.select:
SELECT mst_cd, dtl_cd, dtl_name, dtl_notes
FROM TB_dtl  
WHERE mst_cd = #{mst_cd} 
AND dtl_cd = #{dtl_cd}

mapper.update:
UPDATE TB_dtl
SET mst_cd=#{mst_cd}, dtl_cd=#{dtl_cd}, dtl_name=#{dtl_name}, dtl_notes=#{dtl_notes}, upd_date=NOW()
WHERE mst_cd = #{mst_cd}
AND dtl_cd = #{dtl_cd}

mapper.delete:
Delete from TB_dtl
WHERE mst_cd = #{mst_cd}
AND dtl_cd = #{dtl_cd}

mapper.select:
SELECT COUNT(*)
FROM TB_dtl
WHERE dtl_cd = #{dtl_cd}
AND mst_cd = #{mst_cd}
